import './App.css';
import { useEffect, useState } from 'react';

function App() {
  const [response, setResponse] = useState({});
  const [inputSearch, setInputSearch] = useState('');

  const getApi = () => {
    fetch('https://prototype.sbulltech.com/api/v2/instruments')
      .then(function(response) {
        return response.text();
      }).then(function(data) {
        const arr = data.split('\n');
        let object = {};
        arr.forEach((element, index) => {
          const companyInfo = element.split(',');
          if(companyInfo[0] && index !== 0){
            object = {
              ...object,
              [companyInfo[0]]: {
                symbol: companyInfo[0],
                name: companyInfo[1],
                sector: companyInfo[2],
                validTill: companyInfo[3],
              }
            }; 
          } 
        });

        setResponse(object);
      });
  };

  useEffect(() => {
    getApi();
  }, []);

  console.log('response :: ', inputSearch);

  return (
    <div className="container">
      <header className="header">
      <input
        type="number"
        value={inputSearch}
        onChange={(e) => setInputSearch(e.target.value)}
      />
      </header>
      <div className="App-header">
      <table>
        <tr>
          <th>SYMBOL</th>
          <th>NAME</th>
          <th>SECTOR</th>
        </tr>
        {(Object.keys(response) || []).map((val, key) => {
          return (
            <tr key={key}>
              <td>{response[val].symbol ? response[val].symbol : '-'}</td>
              <td>{response[val].name ? response[val].name : '-'}</td>
              <td>{response[val].sector ? response[val].sector : '-'}</td>
            </tr>
          )
        })}
      </table>
      </div>
    </div>
  );
}

export default App;
