import { useEffect, useState } from "react";
import Fuse from "fuse.js";
import { parse } from 'csv-parse';

const useApp = () => {
  const [response, setResponse] = useState([]);
  const [inputSearch, setInputSearch] = useState("");
  const [searchResult, setSearchResult] = useState([]);
  const [page, setPage] = useState("");

  const getApi = async () => {
    try {
      await fetch("https://prototype.sbulltech.com/api/v2/instruments")
        .then(function (response) {
          return response.text();
        })
        .then(function (data) {
          // const arr = data.split("\n");
          // let array = [];
          // arr.forEach((element, index) => {
          //   const companyInfo = element.split(",");
          //   if (companyInfo[0] && index !== 0) {
          //     array = [
          //       ...array,
          //       {
          //         symbol: companyInfo[0],
          //         name: companyInfo[1],
          //         sector: companyInfo[2],
          //         validTill: companyInfo[3],
          //       },
          //     ];
          //   }
          // });

          // setResponse(array);

          const records = [];

          const parser = parse({
            delimiter: ':'
          });

          parser.on('readable', function(){
            let record;
            while ((record = parser.read()) !== null) {
              records.push(record);
            }
          });

          console.log('records :: ', records);


        });
    } catch (error) {
      console.log("error :: ", error);
    }
  };

  useEffect(() => {
    getApi();
  }, []);

  useEffect(() => {
    const fuse = new Fuse(response, {
      keys: ["symbol", "name"],
      shouldSort: true,
      includeScore: true,
      threshold: 0.5,
    });

    const results = fuse.search(inputSearch);
    setSearchResult(results);
  }, [inputSearch, response]);

  return {
    searchResult,
    inputSearch,
    setInputSearch,
    response,
    page,
    setPage,
  };
};

export default useApp;
