import React, { useEffect } from 'react';
import './styles.css';
import useInstrumentPage from './useInstrumentPage';

const InstrumentPage = ({ page = '', setPage = () => {} }) => {
    const { apiData = {}, refetch = false, setRefetch = () => {} } = useInstrumentPage({ page });

    console.log('apiData :: ', apiData);
    const quotesToShow = apiData?.payload?.[page];

    useEffect(() => {
        const interval = setInterval(() => {
            const date = new Date().toISOString();

            const quotesValid = apiData?.payload?.[page];

            (quotesValid || []).every(element => {
              const arr = element?.valid_till.split(' ');

              if(date.slice(11, 19) > arr[1] && !refetch){
                setRefetch(true);
                return false;
              } else return true;
            });

            console.log('today :: ', date.slice(11, 19));
        }, 1000);

        return () => clearInterval(interval);
      }, [JSON.stringify(apiData)]);

    return (
      <div className="container">
        <div className="header">
          <div className="title">Quotes</div>

          <div className="inputContainer" onClick={() => setPage('')}>
            Back to Instruments Page
          </div>
        </div>

        {(quotesToShow || []).map((element) => {
            return (
              <div className="quote">{element?.price}</div>
            );
        })}
      </div>
    );
};

export default InstrumentPage;
