import { useEffect, useState } from "react";

const useInstrumentPage = ({ page = "" }) => {
  const [apiData, setApiData] = useState({});

  let res = {};

  // const getApi = async () => {
    try {
      res =  fetch(
        `https://prototype.sbulltech.com/api/v2/quotes/${page}`
      );

    } catch (error) {
      console.log("error :: ", error);
    }
  // };

  // useEffect(() => {
  //   getApi();
  // }, []);

  useEffect(() => {
    setApiData( res.json());

  }, [res]);

  const quotesToShow = apiData?.payload?.[page];

  useEffect(() => {
    const interval = setInterval(() => {
      const date = new Date().toISOString();

      (quotesToShow || []).every((element) => {
        const arr = element?.valid_till.split(" ");

        if (date.slice(11, 19) > arr[1]) {
          getApi();

          return false;
        } else return true;
      });
    }, 2000);

    return () => clearInterval(interval);
  }, [apiData]);

  (quotesToShow || []).sort((a, b) => (a.price > b.price ? 1 : -1));

  return {
    apiData,
    quotesToShow,
  };
};

export default useInstrumentPage;
