import React, { useEffect } from 'react';
import './styles.css';
import useInstrumentPage from './useInstrumentPage';

const InstrumentPage = ({ page = '', setPage = () => {} }) => {
    const { apiData = {} } = useInstrumentPage({ page });

    console.log('apiData :: ', apiData);
    const quotesToShow = apiData?.payload?.[page];

    useEffect(() => {
        const interval = setInterval(() => {
            const date = new Date().toISOString();
            console.log('today :: ', date);
              let callApi = false;
            (quotesToShow || []).forEach(element => {
              if(date > element?.valid_till){
                callApi = true;
              }
            });
            // loop
             // expiry check
             // = > 0
             // => call api
             // new set data kidar se milega
        }, 1000);

        return () => clearInterval(interval);
      }, []);

    return (
      <div className="container">
        <div className="header">
          <div className="title">Quotes</div>

          <div className="inputContainer" onClick={() => setPage('')}>
            Back to Instruments Page
          </div>
        </div>

        {(quotesToShow || []).map((element) => {
            return (
              <div className="quote">{element?.price}</div>
            );
        })}
      </div>
    );
};

export default InstrumentPage;
