import { useEffect, useState } from "react";

const useInstrumentPage = ({ page = "" }) => {
  const [apiData, setApiData] = useState({});

  const getApi = async () => {
    try {
      const res = await fetch(
        `https://prototype.sbulltech.com/api/v2/quotes/${page}`
      );

      setApiData(await res.json());
    } catch (error) {
      console.log("error :: ", error);
    }
  };

  useEffect(() => {
    getApi();
  }, []);

  const quotesToShow = apiData?.payload?.[page];

  useEffect(() => {
    const interval = setInterval(() => {
      const date = new Date().toISOString();

      (quotesToShow || []).every((element) => {
        const arr = element?.valid_till.split(" ");

        if (date.slice(11, 19) > arr[1]) {
          getApi();

          return false;
        } else return true;
      });
    }, 2000);

    return () => clearInterval(interval);
  }, [apiData]);

  return {
    apiData,
    quotesToShow: quotesToShow.sort((a, b) => (a.price > b.price ? 1 : -1)),
  };
};

export default useInstrumentPage;
