import './App.css';
import React from 'react';
import useApp from './useApp';
import InstrumentPage from './InstrumentPage';

function App() {
  const { 
    searchResult = [],
    inputSearch = '', 
    setInputSearch = () => {}, 
    response =[], 
    page = '',
    setPage = () => {},
  } = useApp();

  if(page) {
    return <InstrumentPage page={page} setPage={setPage} />;
  }

  const renderTable = () => {
    if(searchResult.length > 0){
      return (searchResult || []).map((val, key) => {
        return (
          <div key={key} className="row">
            <div className="symbol" onClick={() => setPage(val.item.symbol)}>
              {val.item.symbol ? val.item.symbol : '-'}
            </div>
            <div className="name">{val.item.name ? val.item.name : '-'}</div>
            <div className="sector">{val.item.sector ? val.item.sector : '-'}</div>
          </div>
        );
      });
    }

    if(searchResult.length === 0 && inputSearch.length > 0){
      return <div>Empty State</div>;
    };

    return (response || []).map((val, key) => {
      return (
        <div key={key} className="row">
          <div className="symbol" onClick={() => setPage(val.symbol)}>
            {val.symbol ? val.symbol : '-'}
          </div>
          <div className="name">{val.name ? val.name : '-'}</div>
          <div className="sector">{val.sector ? val.sector : '-'}</div>
        </div>
      );
    });
  };

  return (
    <div className="container">
      <div className="header">
        <div className="title">INSTRUMENTS</div>

        <div className="inputContainer">
          <input
            type="text"
            value={inputSearch}
            style={{ width: "370px", height: "24px" }}
            onChange={(e) => setInputSearch(e.target.value)}
            placeholder="Search Symbol/Name"
          />
        </div>
      </div>

      <div className="tableContainer">
        <div className="rowHeader">
          <div className="symbolHeader">Symbol</div>
          <div className="name">Name</div>
          <div className="sector">Sector</div>
        </div>

        {renderTable()}
      </div>
    </div>
  );
}

export default App;
