import './App.css';
import { useEffect, useState } from 'react';
import Fuse from 'fuse.js';

function App() {
  const [response, setResponse] = useState([]);
  const [inputSearch, setInputSearch] = useState('');
  const [searchResult, setSearchResult] = useState([]);

  const getApi = () => {
    fetch('https://prototype.sbulltech.com/api/v2/instruments')
      .then(function(response) {
        return response.text();
      }).then(function(data) {
        const arr = data.split('\n');
        let array = [];
        arr.forEach((element, index) => {
          const companyInfo = element.split(',');
          if(companyInfo[0] && index !== 0){
            array = [
              ...array,
              {
                symbol: companyInfo[0],
                name: companyInfo[1],
                sector: companyInfo[2],
                validTill: companyInfo[3],
              }
            ]; 
          } 
        });

        setResponse(array);
      });
  };

  useEffect(() => {
    getApi();
  }, []);

  useEffect(() => {
    const fuse = new Fuse(response, {
      keys: ['symbol', 'name'],
      shouldSort: true,
      includeScore: true,
      threshold: 0.4,
    });

    const results = fuse.search(inputSearch);
    setSearchResult(results);
  }, [inputSearch, response]);

  const renderTable = () => {
    if(searchResult.length > 0){
      return (searchResult || []).map((val, key) => {
        return (
          <div key={key} className="row">
            <div className="symbol">{val.item.symbol ? val.item.symbol : '-'}</div>
            <div className="name">{val.item.name ? val.item.name : '-'}</div>
            <div className="sector">{val.item.sector ? val.item.sector : '-'}</div>
          </div>
        );
      });
    }

    return (response || []).map((val, key) => {
      return (
        <div key={key} className="row">
          <div className="symbol">{val.symbol ? val.symbol : '-'}</div>
          <div className="name">{val.name ? val.name : '-'}</div>
          <div className="sector">{val.sector ? val.sector : '-'}</div>
        </div>
      );
    });
  };

  return (
    <div className="container">
      <div className="header">
      <div className="title">INSTRUMENTS</div>

        <div className="inputContainer">
          <input
            type="text"
            value={inputSearch}
            style={{ width: "370px", height: "24px" }}
            onChange={(e) => setInputSearch(e.target.value)}
            placeholder="Search Symbol/Name"
          />
        </div>
      </div>

      <div className="tableContainer">
        <div className="rowHeader">
          <div className="symbolHeader">Symbol</div>
          <div className="name">Name</div>
          <div className="sector">Sector</div>
        </div>

        {renderTable()}
      </div>
    </div>
  );
}

export default App;
